import {Box, Button, Heading, IconButton, Input, Stack, Text} from "@chakra-ui/react";
import {useEffect, useState} from "react";

const API_BASE = "http://localhost:8082";

function TaskPage() {
    const [tasks, setTasks] = useState([]);

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const token = localStorage.getItem("token")?.trim();
                const response = await fetch(API_BASE + "/tasks", {
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": "Bearer " + token,
                    },
                });

                if(!response.ok){
                    throw new Error("Failed to fetch tasks")
                }
                const data = await response.json();
                setTasks(data);
            } catch (e){
                alert(e)
            }
        };
        fetchTasks();
    }, []);

    const [name, setName] = useState("")
    const [description, setDescription] = useState("")

    return (
        <Box p={15} maxW="md" mx="auto">
            <Heading mb={"6"} textAlign={"center"} color={"green.500"}>
                Tasks
            </Heading>

            <Stack spacing={2} mb={"6"}>
                {tasks.length === 0 && <Text>No tasks available</Text>}
                {tasks.map((task) => (
                    <Box
                        key={task.id}
                        p={3}
                        borderWidth="1px"
                        borderRadius="md"
                        bg={"green.50"}
                    >
                        <Text fontWeight="bold">{task.name}</Text>
                        <Text>{task.description}</Text>
                        <IconButton aria-label={""} color={"red.50"}></IconButton>
                    </Box>
                ))}
            </Stack>

            <Box>
                <Button colorScheme={"green"} onClick={onOpen}>
                    + Create Task
                </Button>
            </Box>
        </Box>
    );
}

export default TaskPage
